  private static LexicalTable lt;
  private static Hashtable    kwHt;  // hash table for keywords: string -> token 
  private static Hashtable    nHt;   // hash table for names: string -> null 
  private static ArrayList    nl;    // name list  for names, index is spix

  private static String curLineStr;  // current source line
  private static char[] tokenStrArr; // token string in an array
  private static int    tokenStrLen; // length of token string in tokenStrArr
  private static int    state;       // current automaton state
  private static int    apxLen;      // length of appendix in token string
  private static int    pendingEOLs; // nr of EOLs found in comment

  // --- to save and restore scanner state ---
  private static char   savedCh;
  private static int    savedCol, savedLine;

  private static void SaveScannerState() {
    savedCh      = curCh;
    savedCol     = curCol;
    savedLine    = curLine;
  } // SaveScannerState

  private static void RestoreScannerState() {
    tokenStrLen -= apxLen;
    apxLen       = 0;
    curCh        = savedCh;
    curCol       = savedCol;
    curLine      = savedLine;
  } // RestoreScannerState


  public static void NextCh() {
  //-----------------------------------|----------------------------------------
    for (;;) {
      
      if (curCol < curLineStr.Length) {         // within line
        curCol++;
        curCh = curLineStr[curCol - 1];
        return;
      } else if (curCol == curLineStr.Length) { // end of line
        curCol++;
        curCh = Utils.LF; // to separate lines
        return;
      } else { // curCol > curLineStr.Length
        curLineStr = src.ReadLine();
        curLine++;
        curCol = 0;
        if (curLineStr == null) {               // end of file
          curLineStr = "";
          curCh = Utils.EF;
          return;
        } // if
      } // else
    } // for
  } // NextCh


  private static void CommentErr() {
    Errors.LexError(curLine, curCol, "end of file in comment");
  } // CommentErr

  private static bool Comment() {
    
    
    SaveScannerState();
    // --- from '/' '*' to '*' '/' ---
    if (curCh == '/') {
      NextCh();
      if (curCh == '*') {
        NextCh();
        for (;;)
          switch (curCh) {
            case '*':
              NextCh();
              if (curCh == '/') {
                NextCh();
                return true;
              } // if
              break;
            case Utils.EF:
              CommentErr();
              return true;
            default:
              NextCh();
              break;
          } // switch
      } else
        RestoreScannerState();
    } // if
    // --- from '/' '/' to Utils.LF ---
    if (curCh == '/') {
      NextCh();
      if (curCh == '/') {
        NextCh();
        for (;;)
          switch (curCh) {
            case Utils.LF:
              NextCh();
              return true;
            case Utils.EF:
              CommentErr();
              return true;
            default:
              NextCh();
              break;
          } // switch
      } else
        RestoreScannerState();
    } // if
    return false;
  } // Comment

  private static void HandleLexErr() {
    Errors.LexError(curLine, curCol, 
                    "invalid character '{0}' (hex {1:X})", curCh, (int)curCh);
    if (apxLen > 0)
      RestoreScannerState();
  } // HandleLexErr


  public static void GetToken() {
  //-----------------------------------|----------------------------------------
    token = -1;
    tokenStr = null;
    do {
      // --- skip ignored chars and comments ---
      for (;;) {
        while (Sets.member(curCh, lt.ignoredChars))
          NextCh();
        if (!Sets.member(curCh, lt.commentStart))
          break;
        if (!Comment())
          break;
      } // for
      // --- scan for next token ---
      tokenLine   = curLine;
      tokenCol    = curCol;
      tokenStrLen = 0;
      apxLen      = 0;
      state = (curCh == Utils.EF) ? 1 : lt.startTab[curCh];
      for (;;) {
        tokenStrArr[tokenStrLen++]= curCh;
        NextCh();
        switch (state) {
          case 0:
            Errors.LexError(tokenLine, tokenCol, "invalid token start");
            break;
          case 1:
            token = 0;
            tokenStrLen = 0;
            break;                      // EOF recognized
          case 2:
            token = 20;
            break;                      // '{' recognized
          case 3:
            token = 21;
            break;                      // '}' recognized
          case 4:
            token = 22;
            break;                      // ',' recognized
          case 5:
            token = 23;
            break;                      // ';' recognized
          case 6:
            token = 25;
            break;                      // '++' recognized
          case 7:
            token = 26;
            break;                      // '--' recognized
          case 8:
            token = 27;
            break;                      // '<<' recognized
          case 9:
            token = 28;
            break;                      // '>>' recognized
          case 10:
            if (curCh == '|') {
              state = 11;
              continue;
            } // if
            else {
              HandleLexErr();
              break;
            } // else
          case 11:
            token = 29;
            break;                      // '||' recognized
          case 12:
            if (curCh == '&') {
              state = 13;
              continue;
            } // if
            else {
              HandleLexErr();
              break;
            } // else
          case 13:
            token = 30;
            break;                      // '&&' recognized
          case 14:
            token = 31;
            break;                      // '==' recognized
          case 15:
            token = 32;
            break;                      // '!=' recognized
          case 16:
            token = 33;
            break;                      // '<=' recognized
          case 17:
            token = 34;
            break;                      // '>=' recognized
          case 18:
            token = 37;
            break;                      // '*' recognized
          case 19:
            token = 38;
            break;                      // '[' recognized
          case 20:
            token = 39;
            break;                      // ']' recognized
          case 21:
            token = 40;
            break;                      // '%' recognized
          case 22:
            token = 44;
            break;                      // '/' recognized
          case 23:
            token = 45;
            break;                      // '(' recognized
          case 24:
            token = 46;
            break;                      // ')' recognized
          case 25:
            if (Sets.member(curCh, lt.cls[0])) {
              state = 25;
              continue;
            } // if
            else {
              token = 47;
              break;                    // number recognized
            } // else
          case 26:
            if (Sets.member(curCh, lt.cls[0])) {
              state = 26;
              continue;
            } // if
            else if (Sets.member(curCh, lt.cls[1])) {
              state = 26;
              continue;
            } // if
            else if (curCh == '_') {
              state = 26;
              continue;
            } // if
            else {
              token = KeywordCheck();
              if (token >= 0)
                break;                  // keyword recognized
              token = 48;
              break;                    // ident recognized
            } // else
          case 27:
            if (Sets.member(curCh, lt.cls[2])) {
              state = 27;
              continue;
            } // if
            else if (curCh == '\"') {
              state = 28;
              continue;
            } // if
            else {
              HandleLexErr();
              break;
            } // else
          case 28:
            token = 49;
            break;                      // string recognized
          case 29:
            if (Sets.member(curCh, lt.cls[0])) {
              state = 26;
              continue;
            } // if
            else if (Sets.member(curCh, lt.cls[3])) {
              state = 29;
              continue;
            } // if
            else if (Sets.member(curCh, lt.cls[4])) {
              state = 26;
              continue;
            } // if
            else if (curCh == '_') {
              state = 26;
              continue;
            } // if
            else {
              token = KeywordCheck();
              if (token >= 0)
                break;                  // keyword recognized
              token = 48;
              break;                    // ident recognized
            } // else
          case 30:
            if (curCh == '=') {
              state = 15;
              continue;
            } // if
            else {
              token = 41;
              break;                    // '!' recognized
            } // else
          case 31:
            if (curCh == '+') {
              state = 6;
              continue;
            } // if
            else {
              token = 35;
              break;                    // '+' recognized
            } // else
          case 32:
            if (curCh == '-') {
              state = 7;
              continue;
            } // if
            else {
              token = 36;
              break;                    // '-' recognized
            } // else
          case 33:
            if (curCh == '<') {
              state = 8;
              continue;
            } // if
            else if (curCh == '=') {
              state = 16;
              continue;
            } // if
            else {
              token = 43;
              break;                    // '<' recognized
            } // else
          case 34:
            if (curCh == '=') {
              state = 14;
              continue;
            } // if
            else {
              token = 24;
              break;                    // '=' recognized
            } // else
          case 35:
            if (curCh == '=') {
              state = 17;
              continue;
            } // if
            else if (curCh == '>') {
              state = 9;
              continue;
            } // if
            else {
              token = 42;
              break;                    // '>' recognized
            } // else
        } // switch
        break;
      } // for
    } while (token < 0);
    if (tokenStr == null)
      tokenStr = new String(tokenStrArr, 0, tokenStrLen);
  } // GetToken

  private static int KeywordCheck() {
    tokenStr = new String(tokenStrArr, 0, tokenStrLen - apxLen);
    Object token = kwHt[tokenStr];
    return (token == null) ? -1 : (int)token;
  } // KeywordCheck


  public static int Hash(String s) {
  //-----------------------------------|----------------------------------------
    Object spix = nHt[s];
    if (spix == null) {
      if (caseSensitive)
        nl.Add(s);
      else
        nl.Add(s.ToUpper());
      spix   = nl.Count - 1;
      nHt[s] = spix;
    } // if
    return (int)spix;
  } // Hash
  
  
  public static String HashedStr(int spix) {
  //-----------------------------------|----------------------------------------
    return (String)nl[spix];
  } // HashedStr


